mapper.select:
SELECT 

FROM school_home a

WHERE a.id = #{id}

SELECT 

FROM school_home a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		${sqlMap.dsf}


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date ASC

SELECT 

FROM school_home a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE school_home SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
